import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router";
import { Link } from "react-router-dom";

const tmdbKey = "11039f14945ee0f2e0036d42b77c687b"
// Test user & db schema:
/* 
_id: 6369d823852305533b133048

userName: user1

password: password1

hash: passwordhash

salt: hashsalt

movieData: [] 
*/

const MovieCard = (props) => (
  <div className="movieCard">
    <div className='cardOptionsContainer'>
      <div className='viewedCheckContainer'>check</div>
      <div className='favoriteButton'>heart</div>
      <div className='deleteCardContainer'>trashcan</div>
    </div>
    <div className='rankTitleYearContainer'>
      <div className="movieRank">{props.movieData.rank}</div>
      <div className='movieTitleYearContainer'>
        <div className='movieTitle'>{props.movieData.movieTitle}</div>
        <div className='movieYear'>2022</div>
      </div>
    </div>
    <div className='moviePosterContainer'>
      <img src={`https://image.tmdb.org/t/p/original/${props.movieData.posterPath}`} alt="Movie poster" className='posterImg' />
    </div>
    <div className='movieSynopsis'>
      {props.movieData.overview}
    </div>
  </div>
);

export default function MovieCards() {
  const [movieData, setMovieData] = useState([]);

  const initMovieData = require("../initData/initMovieData.json");

  useEffect(() => {
    async function getUserData() {
      const response = await fetch(`http://localhost:5000/userData/`);

      if (!response.ok) {
        const message = `An error occurred: ${response.statusText}`;
        window.alert(message);
        return;
      }

      let userData = await response.json();
      console.log(response);
      console.log(userData);
      let movieData = ((userData || {}).movieData || []);
      setMovieData(movieData);
      console.log(movieData);

      if (movieData.length === 0) {
        setMovieData(initMovieData);
        movieData.map((setChecked) =>
          setChecked.checked = false)
        movieData.map((setFavorite) =>
          setFavorite.favorite = false)

        addData();

        async function addData() {
          for (let i = 0; i < initMovieData.length; i++) {
            const response = await fetch(`https://api.themoviedb.org/3/movie/${initMovieData[i].imdbId}?api_key=${tmdbKey}&language=en-US`);

            const metaData = await response.json();
            console.log(metaData);
            userData.movieData[i].movieTitle = metaData.title;
            userData.movieData[i].posterPath = metaData.poster_path;
            userData.movieData[i].overview = metaData.overview
          }
        }
        setMovieData(movieData);
      }

    }

    getUserData();

    return;
  }, [movieData.length])

  // Method to toggle viewed status

  // Method to toggle favorite


  // This method will delete a movie entry
  async function deleteMovie(id) {
    await fetch(`http://localhost:5000/${id}`, {
      method: "DELETE"
    });

    const newMovieData = movieData.filter((el) => el._id !== id);
    setMovieData(newMovieData);
  }

  function movieCards() {
    return movieData.map((movieData) => {
      return (
        <MovieCard
          movieData={movieData}
          /*           viewed={viewed}
                    favorite={favorite} */
          deleteMovie={() => deleteMovie(movieData._id)}
          key={movieData._id}
        />
      );
    });
  }

  return (
    <div id='movieCardsContainer'>
      {movieCards()}
    </div>
  );
}
